(187, 'theme_mods_hestia', 'a:39:{i:0;b:0;s:18:\"nav_menu_locations\";a:1:{s:7:\"primary\";i:3;}s:28:\"zerif_frontpage_was_imported\";s:9:\"not-zerif\";s:28:\"ti_about_recommended_plugins\";a:1:{s:19:\"themeisle-companion\";s:7:\"visible\";}s:31:\"hestia_feature_thumbnail_buffer\";s:65:\"http://localhost/wordpress/wp-content/uploads/2024/05/unnamed.jpg\";s:18:\"custom_css_post_id\";i:-1;s:24:\"hestia_feature_thumbnail\";s:65:\"http://localhost/wordpress/wp-content/uploads/2024/05/unnamed.jpg\";s:22:\"hestia_big_title_title\";s:74:\"Analiza uticaja drustvenih mreza na odabir destinacija i ponasanje turista\";s:21:\"hestia_big_title_text\";s:14:\"Padine Vitorog\";s:28:\"hestia_big_title_button_text\";s:15:\"pročitaj više\";s:28:\"hestia_big_title_button_link\";s:6:\"#about\";s:20:\"hestia_contact_title\";s:15:\"Kraški fenomen\";s:26:\"hestia_contact_content_new\";s:199:\"<p><a href=\"https://youtu.be/b5Hv7NJONwI\"><img src=\"http://localhost/wordpress/wp-content/uploads/2024/05/japage1-min-1.png\" alt=\"\" width=\"1000\" height=\"666\" class=\"wp-image-121 size-full\" /></a></p>\";s:26:\"hestia_blog_sidebar_layout\";s:10:\"full-width\";s:21:\"hestia_slider_content\";s:458:\"[{\"color\":\"#e91e63\",\"color2\":\"\",\"text\":\"\\u0414\\u0443\\u0433\\u043c\\u0435\",\"link\":\"#\",\"text2\":\"\",\"link2\":\"\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/slider1.jpg\",\"title\":\"Lorem Ipsum\",\"subtitle\":\"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.\",\"social_repeater\":\"undefined\",\"id\":\"customizer_repeater_56d7ea7f40a56\",\"shortcode\":\"undefined\"}]\";s:26:\"disable_frontpage_sections\";b:0;s:16:\"background_image\";s:75:\"http://localhost/wordpress/wp-content/uploads/2024/05/planina_vitorog-6.jpg\";s:21:\"background_position_x\";s:6:\"center\";s:21:\"background_position_y\";s:6:\"center\";s:27:\"hestia_big_title_background\";s:75:\"http://localhost/wordpress/wp-content/uploads/2024/05/planina_vitorog-6.jpg\";s:17:\"hestia_team_title\";s:11:\"Dobrodošli\";s:20:\"hestia_team_subtitle\";s:163:\" Ovde možete pronaći informacije o društvenim mrežama, Vaganskoj pećini, vožnji biciklom i kvadom Kupreškim poljem, kao i gastronomskim ponudama ovog kraja.\";s:23:\"hestia_features_content\";s:1041:\"[{\"icon_value\":\"fab fa-weixin\",\"color\":\"#e91e63\",\"text\":\"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.\",\"link\":\"#\",\"text2\":\"undefined\",\"title\":\"Responsive\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"social-repeater-6636716964977\",\"shortcode\":\"undefined\"},{\"icon_value\":\"fas fa-check\",\"color\":\"#00bcd4\",\"text\":\"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.\",\"link\":\"#\",\"text2\":\"undefined\",\"title\":\"Quality\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"social-repeater-663671691d7e3\",\"shortcode\":\"undefined\"},{\"icon_value\":\"far fa-life-ring\",\"color\":\"#4caf50\",\"text\":\"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.\",\"link\":\"#\",\"text2\":\"undefined\",\"title\":\"Support\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"social-repeater-6636716978ca0\",\"shortcode\":\"undefined\"}]\";s:27:\"hestia_testimonials_content\";s:1380:\"[{\"text\":\"&quot;We have no regrets! After using your product my business skyrocketed! I made back the purchase price in just 48 hours! I couldn&#039;t have asked for more than this.&quot;\",\"link\":\"\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/5.jpg\",\"title\":\"Inverness McKenzie\",\"subtitle\":\"Business Owner\",\"social_repeater\":\"undefined\",\"id\":\"customizer_repeater_56d7ea7f40d56\",\"shortcode\":\"undefined\"},{\"text\":\"&quot;Your company is truly upstanding and is behind its product 100 percent. Hestia is worth much more than I paid. I like Hestia more each day because it makes easier.&quot;\",\"link\":\"\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/6.jpg\",\"title\":\"Hanson Deck\",\"subtitle\":\"Independent Artist\",\"social_repeater\":\"undefined\",\"id\":\"customizer_repeater_56d7ea7f40d66\",\"shortcode\":\"undefined\"},{\"text\":\"&quot;Thank you for making it painless, pleasant and most of all hassle free! I am so pleased with this product. Dude, your stuff is great! I will refer everyone I know.&quot;\",\"link\":\"\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/7.jpg\",\"title\":\"Natalya Undergrowth\",\"subtitle\":\"Freelancer\",\"social_repeater\":\"undefined\",\"id\":\"customizer_repeater_56d7ea7f40d76\",\"shortcode\":\"undefined\"}]\";s:19:\"hestia_team_content\";s:2500:\"[{\"icon_value\":\"\",\"text\":\"Ovde \\u0107emo istra\\u017eiti uticaj dru\\u0161tvenih mre\\u017ea na savremeno dru\\u0161tvo, analizirati pozitivne i negativne aspekte njihove upotrebe i pru\\u017eiti nekoliko saveta za bezbedno kori\\u0161\\u0107enje.\",\"link\":\"http:\\/\\/localhost\\/wordpress\\/2024\\/05\\/03\\/can-curiosity-may-end-shameless-explained\\/\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/uploads\\/2024\\/05\\/images.png\",\"title\":\"Drustvene mreze\",\"subtitle\":\"\",\"social_repeater\":\"\",\"id\":\"customizer_repeater_56d7ea7f40c56\",\"shortcode\":\"undefined\"},{\"icon_value\":\"\",\"text\":\"Upoznajte se sa jednim od najlep\\u0161ih prirodnih fenomena u na\\u0161em kraju - Vaganskom pe\\u0107inom. Saznajte vi\\u0161e o njenom geolo\\u0161kom poreklu, fauni i flori, kao i turisti\\u010dkim mogu\\u0107nostima\",\"link\":\"http:\\/\\/localhost\\/wordpress\\/2024\\/05\\/03\\/appearance-guide\\/?customize_changeset_uuid=d3bdffdf-07ab-4595-9560-cf231e9bf267&amp;customize_autosaved=on&amp;customize_messenger_channel=preview-4\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/uploads\\/2024\\/05\\/vaganska_pecina-1-1.jpg\",\"title\":\"Vaganska pe\\u0107ina\",\"subtitle\":\"\",\"social_repeater\":\"\",\"id\":\"customizer_repeater_56d7ea7f40c66\",\"shortcode\":\"undefined\"},{\"icon_value\":\"\",\"text\":\"Otkrijte avanturisti\\u010dki duh kroz vo\\u017enju biciklom ili kvadom Kupre\\u0161kim poljem. Sa\\u017eivite se sa prirodom i u\\u017eivajte u prelepim pejza\\u017eima ovog podru\\u010dja.\",\"link\":\"http:\\/\\/localhost\\/wordpress\\/2024\\/05\\/03\\/perfectly-on-furniture\\/\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/uploads\\/2024\\/05\\/quad_kupres_-1.jpg\",\"title\":\"Vo\\u017enja bickilom i kvadom Kupre\\u0161kim poljem\",\"subtitle\":\"\",\"social_repeater\":\"\",\"id\":\"customizer_repeater_56d7ea7f40c76\",\"shortcode\":\"undefined\"},{\"icon_value\":\"\",\"text\":\"Zavirite u bogatu gastronomsku ponudu na\\u0161eg kraja. Upoznajte se sa tradicionalnim jelima i lokalnim specijalitetima koji \\u0107e zadovoljiti i najprobirljivije gurmane.\",\"link\":\"http:\\/\\/localhost\\/wordpress\\/2024\\/05\\/03\\/fat-son-how-smiling-natural\\/?customize_changeset_uuid=05262dab-c867-40ba-b880-0352f797a24d&amp;customize_messenger_channel=preview-1\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/uploads\\/2024\\/05\\/20170122_141810_1486304024164_resized.jpg\",\"title\":\"Gastro ponude ovoga kraja \",\"subtitle\":\"\",\"social_repeater\":\"\",\"id\":\"customizer_repeater_56d7ea7f40c86\",\"shortcode\":\"undefined\"}]\";s:26:\"hestia_clients_bar_content\";s:1706:\"[{\"text\":\"undefined\",\"link\":\"#\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/plugins\\/themeisle-companion\\/obfx_modules\\/companion-legacy\\/inc\\/hestia\\/inc\\/img\\/clients1.png\",\"title\":\"undefined\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"social-repeater-66367169171e6\",\"shortcode\":\"undefined\"},{\"text\":\"undefined\",\"link\":\"#\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/plugins\\/themeisle-companion\\/obfx_modules\\/companion-legacy\\/inc\\/hestia\\/inc\\/img\\/clients2.png\",\"title\":\"undefined\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"social-repeater-66367169b4db6\",\"shortcode\":\"undefined\"},{\"text\":\"undefined\",\"link\":\"#\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/plugins\\/themeisle-companion\\/obfx_modules\\/companion-legacy\\/inc\\/hestia\\/inc\\/img\\/clients3.png\",\"title\":\"undefined\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"social-repeater-6636716929010\",\"shortcode\":\"undefined\"},{\"text\":\"undefined\",\"link\":\"#\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/plugins\\/themeisle-companion\\/obfx_modules\\/companion-legacy\\/inc\\/hestia\\/inc\\/img\\/clients4.png\",\"title\":\"undefined\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"social-repeater-66367169ff801\",\"shortcode\":\"undefined\"},{\"text\":\"undefined\",\"link\":\"#\",\"text2\":\"undefined\",\"image_url\":\"http:\\/\\/localhost\\/wordpress\\/wp-content\\/plugins\\/themeisle-companion\\/obfx_modules\\/companion-legacy\\/inc\\/hestia\\/inc\\/img\\/clients5.png\",\"title\":\"undefined\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"social-repeater-6636716940fe3\",\"shortcode\":\"undefined\"}]\";s:20:\"hestia_features_hide\";b:1;s:24:\"hestia_testimonials_hide\";b:1;s:23:\"hestia_clients_bar_hide\";b:1;s:17:\"hestia_blog_title\";s:10:\"Aktivnosti\";s:19:\"hestia_font_subsets\";a:1:{i:0;s:5:\"latin\";}s:20:\"hestia_blog_subtitle\";s:46:\"Pogledajte šta sve možete raditi kao turista\";s:23:\"hestia_header_alignment\";s:4:\"left\";s:21:\"hestia_search_in_menu\";b:1;s:20:\"hestia_header_layout\";s:7:\"default\";s:30:\"hestia_alternative_blog_layout\";s:23:\"blog_alternative_layout\";s:25:\"hestia_contact_background\";s:69:\"http://localhost/wordpress/wp-content/uploads/2024/05/japage3-min.png\";s:25:\"hestia_contact_area_title\";s:37:\"Kliknite na sliku i pogledajte video!\";s:23:\"hestia_contact_subtitle\";s:6:\"Japage\";}', 'yes'),
