(64, 1, '2024-05-03 20:27:04', '2024-05-03 18:27:04', '<!-- wp:cover {\"overlayColor\":\"white\",\"isUserOverlayColor\":true,\"align\":\"full\",\"className\":\"is-light hestia-clear-top-padding\"} -->\n<div class=\"wp-block-cover alignfull is-light hestia-clear-top-padding\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-white-background-color has-background-dim-100 has-background-dim\"></span><div class=\"wp-block-cover__inner-container\"><!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"color\":{\"text\":\"#3c4858\"}}} -->\n<h2 class=\"wp-block-heading has-text-align-center has-text-color\" style=\"color:#3c4858\">Društvene mreže</h2>\n<!-- /wp:heading -->\n\n<!-- wp:spacer {\"height\":\"40px\"} -->\n<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:columns {\"className\":\"container\"} -->\n<div class=\"wp-block-columns container\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:gallery {\"linkTo\":\"none\",\"align\":\"center\"} -->\n<figure class=\"wp-block-gallery aligncenter has-nested-images columns-default is-cropped\"><!-- wp:image {\"id\":8,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://localhost/wordpress/wp-content/uploads/2024/05/images.png\" alt=\"\" class=\"wp-image-8\"/></figure>\n<!-- /wp:image --></figure>\n<!-- /wp:gallery -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":5,\"style\":{\"color\":{\"text\":\"#3c4858\"},\"typography\":{\"fontSize\":18}}} -->\n<h5 class=\"wp-block-heading has-text-align-center has-text-color\" style=\"color:#3c4858;font-size:18px\">Uticaj</h5>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"color\":{\"text\":\"#999999\"},\"typography\":{\"fontSize\":14}}} -->\n<p class=\"has-text-align-center has-text-color\" style=\"color:#999999;font-size:14px\">Društvene mreže danas imaju ogroman uticaj na promociji turističkih destinacija.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"25px\"} -->\n<div style=\"height:25px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:gallery {\"linkTo\":\"none\",\"align\":\"center\"} -->\n<figure class=\"wp-block-gallery aligncenter has-nested-images columns-default is-cropped\"><!-- wp:image {\"id\":113,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://localhost/wordpress/wp-content/uploads/2024/05/105990512_gettyimages-952414660.jpg\" alt=\"\" class=\"wp-image-113\"/></figure>\n<!-- /wp:image --></figure>\n<!-- /wp:gallery -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":5,\"style\":{\"color\":{\"text\":\"#3c4858\"},\"typography\":{\"fontSize\":18}}} -->\n<h5 class=\"wp-block-heading has-text-align-center has-text-color\" style=\"color:#3c4858;font-size:18px\">Mogućnosti</h5>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"color\":{\"text\":\"#999999\"},\"typography\":{\"fontSize\":14}}} -->\n<p class=\"has-text-align-center has-text-color\" style=\"color:#999999;font-size:14px\">Društvene mreže daju mogućnosst ljudima koji su posjetili neku destinaciju da podjele svoja iskustva. Takođe, omogućavaju interakciju sa svojim korisnicima, kao i kreiranje sopstvenih sadržaja.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"25px\"} -->\n<div style=\"height:25px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:gallery {\"linkTo\":\"none\",\"align\":\"center\"} -->\n<figure class=\"wp-block-gallery aligncenter has-nested-images columns-default is-cropped\"><!-- wp:image {\"id\":114,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://localhost/wordpress/wp-content/uploads/2024/05/drustvene-mreze-koncept-nbgcreator.jpg\" alt=\"\" class=\"wp-image-114\"/></figure>\n<!-- /wp:image --></figure>\n<!-- /wp:gallery -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":5,\"style\":{\"color\":{\"text\":\"#3c4858\"},\"typography\":{\"fontSize\":18}}} -->\n<h5 class=\"wp-block-heading has-text-align-center has-text-color\" style=\"color:#3c4858;font-size:18px\">Ciljevi</h5>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"color\":{\"text\":\"#999999\"},\"typography\":{\"fontSize\":14}}} -->\n<p class=\"has-text-align-center has-text-color\" style=\"color:#999999;font-size:14px\">Cilj društvenih mreža je okupljanje ljudi na jedno,, mjestu, što je veoma bitno za promociju turizma..</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"25px\"} -->\n<div style=\"height:25px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:spacer {\"height\":\"40px\"} -->\n<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:columns {\"className\":\"container\"} -->\n<div class=\"wp-block-columns container\"><!-- wp:column {\"className\":\"col-md-5\"} -->\n<div class=\"wp-block-column col-md-5\"><!-- wp:spacer {\"height\":\"55px\"} -->\n<div style=\"height:55px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading -->\n<h2 class=\"wp-block-heading\">Turizam u Republici Srpskoj</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Za detaljnije informacije ostanite na našoj stranici</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"className\":\"col-md-6 col-md-offset1\"} -->\n<div class=\"wp-block-column col-md-6 col-md-offset1\"><!-- wp:spacer {\"height\":\"55px\"} -->\n<div style=\"height:55px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:image {\"id\":118,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/wordpress/wp-content/uploads/2024/05/images.jpg\" alt=\"\" class=\"wp-image-118\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->', 'Home', '', 'publish', 'closed', 'closed', '', 'home', '', '', '2024-05-11 17:05:25', '2024-05-11 15:05:25', '', 0, 'http://localhost/wordpress/?page_id=64', 0, 'page', '', 0),
(65, 1, '2024-05-03 20:27:04', '2024-05-03 18:27:04', '<!-- wp:columns {\"align\":\"full\"} -->\n<div class=\"wp-block-columns alignfull\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:html -->\n<div style=\"width: 100%\">\n    <iframe width=\"100%\" height=\"400\" frameborder=\"0\" scrolling=\"no\" marginheight=\"0\" marginwidth=\"0\" \n    src=\"https://maps.google.com/maps?width=100%25&amp;height=400&amp;hl=en&amp;q=Vaganska%20pećina,%20Vagan,%20Šipovo&amp;t=&amp;z=14&amp;ie=UTF8&amp;iwloc=B&amp;output=embed\">\n    </iframe>\n</div>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<div style=\"width: 100%\">\n    <iframe width=\"100%\" height=\"400\" frameborder=\"0\" scrolling=\"no\" marginheight=\"0\" marginwidth=\"0\" \n    src=\"https://maps.google.com/maps?width=100%25&amp;height=400&amp;hl=en&amp;q=Kupreško%20polje,%20Blagaj&amp;t=&amp;z=14&amp;ie=UTF8&amp;iwloc=B&amp;output=embed\">\n    </iframe>\n</div>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns {\"className\":\"container\"} -->\n<div class=\"wp-block-columns container\"><!-- wp:column {\"verticalAlignment\":\"center\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:heading {\"className\":\"hestia-title\"} -->\n<h2 class=\"wp-block-heading hestia-title\"></h2>\n<!-- /wp:heading -->\n\n<!-- wp:spacer {\"height\":\"40px\"} -->\n<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"verticalAlignment\":\"center\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"50%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:50%\"></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:image {\"id\":184,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/wordpress/wp-content/uploads/2024/05/images-1.jpg\" alt=\"\" class=\"wp-image-184\"/></figure>\n<!-- /wp:image -->', 'Contact', '', 'publish', 'closed', 'closed', '', 'contact', '', '', '2024-05-31 19:48:05', '2024-05-31 17:48:05', '', 0, 'http://localhost/wordpress/?page_id=65', 0, 'page', '', 0),
